import React from 'react';
import classnames from 'classnames';
import Modal from './Modal';

class Allusers extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data : [],
            deletedUser:{},
            userToDelete: undefined,
            showInfo: false
        };


        this.deleteUser = this.deleteUser.bind(this)
    }
    componentDidMount() {
        fetch('http://localhost:8000/user/get', {
            method: "GET",
            headers: new Headers({
                'Authorization' : "Bearer " + localStorage.getItem('token')
            })
        })
        .then(response => {
            response.json().then(json => {
                if(response.status === 401) {
                    localStorage.clear();
                    window.location.href = '/login';
                }
                if(json.status === "succes"){
                    this.setState({
                        data: json.data
                    })
                } else {
                    console.log(json.data)
                }
            })
        })
        .catch(function(error) {
            console.log('Request failed', error)
        });
    }

    deleteUser(id) {
        fetch('http://localhost:8000/user/delete/' + id, {
            method: "DELETE",
            headers: new Headers({
                'Authorization' : "Bearer " + localStorage.getItem('token')
            })
        })
        .then(response => {
            response.json().then(json => {
                if(response.status === 401) {
                    localStorage.clear();
                    window.location.href = '/login';
                }
                if(json.status === "succes"){
                    console.log(json.data)
                    this.setState({
                        deletedUsed: json.data,
                        showInfo: true
                    })
                } else {
                    console.log(json.data)
                }
            })
        })
    }

    render() {
        return (
            <div className={classnames('allusers', this.props.styleName)}>
                <div className="alert alert-info text-center" htmlrole="alert" style={{display: this.state.showInfo ? 'block': 'none'}}>
                    User {this.state.deletedUser.name} {this.state.deletedUser.surname} succesfully deleted.
                </div>
                <Modal styleName="fade" title="Are you sure?" button="Delete" id="usermodal" parentFunc={this.deleteUser} item={this.state.userToDelete}>
                    <p>Do you want to delete this user. The change is irreversible.</p>
                </Modal>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Surname</th>
                            <th scope="col">Email</th>
                            <th  className="d-flex justify-content-center ">
                                <form className="form-inline">
                                    <div className="input-group">
                                        <input className="form-control " type="search" placeholder="Search" aria-label="Search "/>
                                        <div className="input-group-append">
                                            <button className="btn btn-outline-success"  type="button">Search</button> 
                                        </div>
                                    </div>
                                </form>
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.data.map((user, i) => {
                                return (
                                    <tr key={user.id.toString()}>
                                        <th scope="row">{i + 1}</th>
                                        <td>{ user.name }</td>
                                        <td>{ user.surname }</td>
                                        <td>{ user.email }</td>
                                        <td className="text-center">
                                            <button type="button" onClick={()=> { this.setState({userToDelete: user.id}) }} className="btn btn-danger" data-toggle="modal" data-target="#usermodal">Delete</button>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            </div>
    );
 }

}


export default Allusers;
