import React from 'react';
import classnames from 'classnames';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

class Search extends React.Component {

  constructor(props) {
    super(props);

  }


  render() {
   return (
     <div className={classnames("history", this.props.styleName) } style={this.props.style}>
        <form className="form-inline d-flex d-lg-none justify-content-center mb-2" onSubmit={this.props.filter}>
            <div className="input-group">
                <input className="form-control " type="search" placeholder="Search" 
                aria-label="Search " onChange={(e) => { this.setState({ nameFilter: e.target.value }) }}/>
                <div className="input-group-append">
                        <button type="submit" className="btn btn-outline-success" ><FontAwesomeIcon icon={faSearch}/></button> 
                </div>
            </div>
            {this.props.children}
        </form>
     </div>
   );
 }

}


export default Search;
