import React from 'react';
import classnames from 'classnames';
import Search from './Search';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSyncAlt } from '@fortawesome/free-solid-svg-icons'

class History extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data : [],
            copyData: [],
        }; 
    }

    filterData = (e) => {
        e.preventDefault()
        const data = this.state.data.filter(d => {
            return d.name.toLowerCase().includes(this.state.nameFilter.toLowerCase());
        });

        this.setState({
            copyData: data
        });
    }

    handleSearch = (e) => {
        this.setState({ nameFilter: e.target.value }) 
    }

  render() {
   return (
     <div className={classnames("History row", this.props.styleName) } style={this.props.style}>
        <div className="row justify-content-center">
            <div className="col-12">
            <Search filter={this.filterData} changeHandler={this.handleSearch} styleName="form-inline mb-2">
                <button className="btn btn-outline-success ml-2"  type="button" onClick={this.loadEvents}><FontAwesomeIcon icon={faSyncAlt} /></button>
            </Search>
            </div>
        </div>
     </div>
   );
 }

}


export default History;
