import React from 'react';
import logo from '../img/logo.jpg';

class Header extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            admin: 0
        };
    }

    getUserData = (e) => {
        fetch('http://localhost:8000/user/login', {
            method: "GET",
            heders: {
                'Authorization' : "B"
            }

        })
        .then(response => response.json())
        .then(data => {
            if(data.status == "succes"){
                localStorage.setItem('token', data.data)
                window.location.href = '/';
            }else {
                this.setState({
                    err: data.data,
                    showErr: true
                })
            }

        })
        .catch(function(error) {
            console.log('Request failed', error)
        });
    }

    logout = (e) => {
        localStorage.clear();
        window.location.href = '/login';
    }

    render() {
        return (
            <nav class="navbar navbar-expand-lg navbar-light bg-light fixed-top">
                <a class="navbar-brand" href="/"><img src={logo} height="50" width="50"/>BeerMail</a>
                <button class="navbar-toggler" data-target="#my-nav" data-toggle="collapse" aria-controls="my-nav" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>
                <div id="my-nav" class="collapse navbar-collapse">
                    <ul class="navbar-nav mr-auto">
                    <li class="nav-item active">
                        <a class="nav-link" href="/admin">Admin<span class="sr-only">(current)</span></a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Item 2</a>
                    </li>
                    </ul>
                    <span class="navbar-text">
                        <ul class="navbar-nav mr-auto">
                        <li class="nav-item active" onClick={this.logout}>
                            <a class="nav-link" >Wyloguj</a>
                        </li>
                        </ul>
                    </span>
                </div>
            </nav>
        );
    }

}

export default Header;
