import React from 'react';
import { Redirect } from 'react-router'

class Auth extends React.Component {
    checkAuth() {
        const token = localStorage.getItem('token');
        if (!token) {
            return <Redirect to='/login' />
        }
    }

    render() {
        this.checkAuth();
        return null;
    }
}

export default Auth;
