import React from 'react';
import classnames from 'classnames';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSyncAlt } from '@fortawesome/free-solid-svg-icons'
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import { faSearch } from '@fortawesome/free-solid-svg-icons'


class Search extends React.Component {

  constructor(props) {
    super(props);
    this.doSearch = this.doSearch.bind(this)
    this.doRefresh = this.doRefresh.bind(this)
    this.doAdd = this.doAdd.bind(this)
  }

  doSearch(){
    if(this.props.parentFunc){
        this.props.searchFunc(this.props.item)
    } 
  }
  doRefresh(){
    if(this.props.parentFunc){
        this.props.refreshFunc(this.props.item)
    } 
  }
  doAdd(){
    if(this.props.parentFunc){
        this.props.addFunc(this.props.item)
    } 
  }



  render() {
   return (
    <form className={classnames(this.props.styleName)} >
        <div className="input-group">
            <input className="form-control " type="search" placeholder="Search" aria-label="Search " onChange={(e) => { this.setState({ nameFilter: e.target.value }) }}/>
            <div className="input-group-append">
                <button className="btn btn-outline-success" onClick={this.doSearch}  type="button"><FontAwesomeIcon icon={faSearch} /></button> 
            </div>                                
        </div>    
        <button className="btn btn-outline-success ml-2"  type="button" onClick={this.doRefresh}><FontAwesomeIcon icon={faSyncAlt} /></button>
        <button className="btn btn-outline-success ml-2"  type="button" onClick={this.doAdd}><FontAwesomeIcon icon={faPlus} /></button>
    </form>
   );
 }

}


export default Search;
